mapper.select:
SELECT *
FROM ${tableName}
WHERE 1 = 1
  and #{date} >= startTime
  and date_add(overTime, INTERVAL 1 day) >= #{date}

mapper.update:
UPDATE ${tableName}


        name = #{newTE.name},


        startTime = #{newTE.startTime},


        overTime = #{newTE.overTime},


        setInViewPage = #{newTE.setInViewPage},


        text = #{newTE.text},


        completed = #{newTE.completed}


WHERE id = #{newTE.id}
